targets.queryText:
query {
        port(livestatusds: "LiveStatus", influxds: "InfluxdbDatasource", 
          		measurement: "Interface-check-command", 
          		filter: "host_name ~ (${hostname:pipe}) and display_name ~ IfCheck-$service", 
              start: $__from, stop: $__to, timezone: "Asia/Bangkok") {

              host_name
              bandwidth {   display_name
                						max_inbits_value {
                                                time
                                                value
                                             }
                						max_outbits_value{
                              									time
                              									value
                            								 }
                    		}
              }
    	}

